import { Component, OnInit } from "@angular/core"
import { Curso } from "./curso";

@Component({
    selector: 'app-curso-list',
    templateUrl: './curso-list.html'
})

export class CursoListComponent implements OnInit {

    cursos: Curso[] = [];

    ngOnInit(): void{
        this.cursos = [
            {
                id: 1,
                name: 'Angular: Forms',
                imageUrl: '',
                price: 99.99,
                code: 'XPS-1234',
                duration: 120,
                rating: 5.4
            },
            {
                id: 2,
                name: 'Angular: HTML',
                imageUrl: '',
                price: 49.99,
                code: 'LKL-5678',
                duration: 50,
                rating: 5.0
            }
        ]
    }
}
