import { Component, OnChanges, Input } from '@angular/core';

@Component({
    selector: 'app-star'
})

export class StarComponent implements OnChanges {
    @Input()
    rating: number = 0;

    starWidth: number;

    ngOnChanges(): void {
        this.starWidth
    }

}
